package com.bayviewglen.dayfour;

public class ExampleOne {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(4+3); //yields 7 because both operands are integers
		
		System.out.println(4.1 + 3); //yields a double because one of the operands is a double 
		
		
		System.out.println(4/3);  //yields an integer because both operands are integers
		
		System.out.println(2/3);  //0
		
		System.out.println(23%2); //1 so it must be odd
		
		System.out.println(83524%10);
		
		
	}

}
